import { Link } from "react-router-dom";
import logoJpg from "../logo.jpg";

const linksList = [
  { id: 1, text: "Home", url: "/" },
  { id: 2, text: "About", url: "/about" },
  { id: 3, text: "Tours", url: "/tours" },
  { id: 4, text: "Services", url: "/services" },
  { id: 5, text: "Blog", url: "/blog" },
  { id: 6, text: "Contact", url: "/contact" },
];

function Header({ activeLink, setActiveLink }) {
  window.onload = () => {
    if (window.location.pathname !== "/") {
      window.location.reload();
      window.location.href = "/";
      setActiveLink("Home");
    }
  };

  useEffect(() => {
    // Get the last part of the path as the active link (e.g., "/about" -> "About")
    const currentPath = location.pathname;
    const link =
      currentPath === "/"
        ? "Home"
        : currentPath.slice(1).charAt(0).toUpperCase() + currentPath.slice(2);
    setActiveLink(link);
  }, [location]);

  return (
    <header className="header">
      <div>
        <Link to="/">
          <img className="logo" src={logoJpg} alt="Logo" />
        </Link>
      </div>

      <div className="main-menu">
        <ul className="links-list">
          {linksList.map((link) => (
            <li key={link.id}>
              <Link
                to={link.url}
                className={activeLink === link.text ? "active" : ""}
                onClick={() => setActiveLink(link.text)}
              >
                {link.text}
              </Link>
            </li>
          ))}
        </ul>
      </div>
    </header>
  );
}

export default Header;
