import { Link, useNavigate } from "react-router-dom";
import logoJpg from "../logo.jpg";

const linksList = [
  { id: 1, text: "Home", url: "/" },
  { id: 2, text: "About", url: "/about" },
  { id: 3, text: "Tours", url: "/tours" },
  { id: 4, text: "Services", url: "/services" },
  { id: 5, text: "Blog", url: "/blog" },
  { id: 6, text: "Contact", url: "/contact" },
];

function Header({ activeLink, setActiveLink }) {
  window.onload = () => {
    if (window.location.pathname !== "/") {
      window.location.reload();
      window.location.href = "/";
      setActiveLink("Home");
    }
  };

  const navigate = useNavigate();

  // Find the index of the current active link in the linksList
  const currentLinkIndex = linksList.findIndex(
    (link) => link.text === activeLink
  );

  // Handle previous and forward arrows
  const goToPreviousLink = () => {
    if (currentLinkIndex > 0) {
      const previousLink = linksList[currentLinkIndex - 1];
      setActiveLink(previousLink.text); // Update active link state
      navigate(previousLink.url); // Navigate to the previous link
    }
  };

  const goToNextLink = () => {
    if (currentLinkIndex < linksList.length - 1) {
      const nextLink = linksList[currentLinkIndex + 1];
      setActiveLink(nextLink.text); // Update active link state
      navigate(nextLink.url); // Navigate to the next link
    }
  };

  return (
    <header className="header">
      <div>
        <Link to="/">
          <img className="logo" src={logoJpg} alt="Logo" />
        </Link>
      </div>

      <div className="main-menu">
        <ul className="links-list">
          {linksList.map((link) => (
            <li key={link.id}>
              <Link
                to={link.url}
                className={activeLink === link.text ? "active" : ""}
                onClick={() => setActiveLink(link.text)}
              >
                {link.text}
              </Link>
            </li>
          ))}
        </ul>
      </div>

      <div className="nav-arrows">
        <button
          onClick={goToPreviousLink}
          disabled={currentLinkIndex === 0} // Disable if on the first link
          className="prev-arrow"
        >
          &larr; Previous
        </button>
        <button
          onClick={goToNextLink}
          disabled={currentLinkIndex === linksList.length - 1} // Disable if on the last link
          className="next-arrow"
        >
          Next &rarr;
        </button>
      </div>
    </header>
  );
}

export default Header;
