import React from "react";
import "../styles/Home.css";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import {
  faEarthAmerica,
  faUsersBetweenLines,
  faTrophy,
} from "@fortawesome/free-solid-svg-icons";

const infoArray = [
  {
    id: 1,
    iconName: faEarthAmerica,
    mainText: "10+ tours",
    text: "This is how we make progress and thrive for success",
  },

  {
    id: 2,
    iconName: faUsersBetweenLines,
    mainText: "Private tours",
    text: "Private tours with private arrangements",
  },

  {
    id: 3,
    iconName: faTrophy,
    mainText: "Best team around the globe",
    text: "Friendly, pedantical, humorous",
  },
];

function Home() {
  return (
    <div className="home-hero">
      <div className="hero-content">
        <h2>
          Let's travel together and have{" "}
          <span className="text-orange">unforgetful</span>{" "}
          <span className="text-blue">moments!</span>
        </h2>
      </div>

      <div class="grid-3-cols tour-basics">
        {infoArray.map((info) => (
          <div class="info-container">
            <FontAwesomeIcon icon={info.iconName} />
            <div>
              <h3>{info.mainText}</h3>
              <p>{info.text}</p>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}

export default Home;
