import { Link, useLocation, NavLink } from "react-router-dom";
import { useState, useEffect } from "react";
import logoJpg from "../logo.jpg";

const linksList = [
  { id: 1, text: "Home", url: "/" },
  { id: 2, text: "About", url: "/about" },
  { id: 3, text: "Tours", url: "/tours" },
  { id: 4, text: "Blog", url: "/blog" },
  { id: 5, text: "Contact", url: "/contact" },
];

function Header() {
  const location = useLocation();
  const [activeLink, setActiveLink] = useState("");

  useEffect(() => {
    const currentPath = location.pathname;
    const active = linksList.find((link) => link.url === currentPath);
    if (active) {
      setActiveLink(active.text);
    } else if (currentPath.startsWith("/tour")) {
      setActiveLink("Tours");
    }
  }, [location.pathname]);

  return (
    <header className="header">
      <div>
        <Link to="/">
          <img className="logo" src={logoJpg} alt="Logo" />
        </Link>
      </div>

      <div className="main-menu">
        <ul className="links-list">
          {linksList.map((link) => (
            <li key={link.id}>
              <NavLink
                to={link.url}
                className={activeLink === link.text ? "active" : ""}
                onClick={() => setActiveLink(link.text)}
              >
                {link.text}
              </NavLink>
            </li>
          ))}
        </ul>
      </div>
    </header>
  );
}

export default Header;
