import React from "react";
import "../styles/Home.css";
import { useState } from "react";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import {
  faEarthAmerica,
  faUsersBetweenLines,
  faTrophy,
  faLocationDot,
  faClock,
  faMoneyBill,
} from "@fortawesome/free-solid-svg-icons";
import toledoImage from "../imgs/toledo.jpg";

const infoArray = [
  {
    id: 1,
    iconName: faEarthAmerica,
    mainText: "10+ tours",
    text: "This is how we progress and thrive for success",
  },

  {
    id: 2,
    iconName: faUsersBetweenLines,
    mainText: "Private tours",
    text: "Private tours with private arrangements",
  },

  {
    id: 3,
    iconName: faTrophy,
    mainText: "Best team around the globe",
    text: "Friendly, pedantical, humorous",
  },
];

function Home() {
  return (
    <div className="home">
      <div className="home-hero">
        <div className="hero-content">
          <h2>Let's travel together and have unforgetful moments!</h2>
        </div>
        <BasicInfo />
        <Tours />
      </div>
    </div>
  );
}

function BasicInfo() {
  return (
    <div>
      <div class="grid-3-cols tour-basics">
        {infoArray.map((info) => (
          <div class="info-container">
            <FontAwesomeIcon className="icon" icon={info.iconName} />
            <div class="text-container">
              <h3>{info.mainText}</h3>
              <p>{info.text}</p>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}

function Tours() {
  const [popularTours, setPopularTours] = useState([
    {
      id: 1,
      image: toledoImage,
      title: "Toledo Explorer",
      location: "Spain",
      duration: "5 days",
      price: "€763",
    },

    {
      id: 2,
      image: "imgs/",
      title: "Dubai Adventure",
      location: "United Arab Emirates",
      duration: "8 days",
      price: "€1,522",
    },

    {
      id: 3,
      image: "imgs/",
      title: "Greek Islands",
      location: "Santorini, Greece",
      duration: "7 days",
      price: "€1,462",
    },
  ]);

  return (
    <section className="popular-tours">
      <h2>Popular Tours</h2>
      <div className="tours-grid">
        {popularTours.map((tour) => (
          <div key={tour.id} className="tour-card">
            <img src={tour.image} alt={tour.title} />
            <div className="tour-info">
              <h3>{tour.title}</h3>
              <div className="tour-details">
                <p>
                  <FontAwesomeIcon icon={faLocationDot} />
                  {tour.location}
                </p>
                <p>
                  <FontAwesomeIcon icon={faClock} />
                  {tour.duration}
                </p>
                <p>
                  <FontAwesomeIcon icon={faMoneyBill} /> {tour.price}
                </p>
              </div>
              <button className="book-now">Book Now</button>
            </div>
          </div>
        ))}
      </div>
    </section>
  );
}

export default Home;
