import { NavLink } from "react-router-dom";
import { useLocation } from "react-router-dom";
import logoJpg from "../logo.jpg";

const linksList = [
  { id: 1, text: "Home", url: "/" },
  { id: 2, text: "About", url: "/about" },
  { id: 3, text: "Tours", url: "/tours" },
  { id: 4, text: "Services", url: "/services" },
  { id: 5, text: "Blog", url: "/blog" },
  { id: 6, text: "Contact", url: "/contact" },
];

function Header() {
  const location = useLocation();

  const currentPath = location.pathname;

  // If you still want a custom active link name:
  const activeLink =
    linksList.find((link) => link.url === currentPath)?.text || "";
  return (
    <header className="header">
      <div>
        <NavLink to="/" end>
          <img className="logo" src={logoJpg} alt="Logo" />
        </NavLink>
      </div>

      <div className="main-menu">
        <ul className="links-list">
          {linksList.map((link) => (
            <li key={link.id}>
              <NavLink
                to={link.url}
                end={link.url === "/"}
                className={({ isActive }) => (isActive ? "active" : "")}
              >
                {link.text}
              </NavLink>
            </li>
          ))}
        </ul>
      </div>
    </header>
  );
}

export default Header;
