import React from 'react';
import { useParams } from 'react-router-dom';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import {
  faLocationDot,
  faClock,
  faMoneyBill,
  faCalendar,
  faUtensils,
  faHotel,
  faPlane,
  faCheck
} from '@fortawesome/free-solid-svg-icons';
import '../../styles/Tour Styles/TourDetails.css';

const tourDetails = {
  1: {
    title: "Toledo Explorer",
    location: "Spain",
    duration: "5 days",
    price: "â‚¬763",
    description: "Explore the historic city of Toledo, known for its rich cultural heritage and stunning architecture. Visit medieval monuments, learn about traditional crafts, and enjoy authentic Spanish cuisine.",
    included: [
      "Professional English speaking guide",
      "4 nights in 4-star hotel",
      "All breakfasts and dinners",
      "Entrance to Toledo Cathedral",
      "Wine tasting experience"
    ],
    highlights: [
      "Visit to Toledo Cathedral",
      "Traditional sword-making workshop",
      "El Greco Museum tour",
      "Local wine tasting",
      "Medieval city walls walk"
    ],
    itinerary: [
      {
        day: 1,
        title: "Arrival & Welcome",
        activities: "Airport transfer, hotel check-in, welcome dinner"
      },
      {
        day: 2,
        title: "Historic Toledo",
        activities: "Cathedral visit, Jewish Quarter tour"
      },
      {
        day: 3,
        title: "Art & Craft",
        activities: "El Greco Museum, sword-making workshop"
      },
      {
        day: 4,
        title: "Culture & Taste",
        activities: "City walls walk, wine tasting, farewell dinner"
      },
      {
        day: 5,
        title: "Departure",
        activities: "Breakfast, airport transfer"
      }
    ]
  },
  // Add similar details for other tours
};

function TourDetails() {
  const { id } = useParams();
  const tour = tourDetails[id];

  if (!tour) {
    return <div className="error-message">Tour not found</div>;
  }

  return (
    <div className="tour-details-container">
      <div className="tour-header">
        <h1>{tour.title}</h1>
        <div className="tour-meta">
          <span><FontAwesomeIcon icon={faLocationDot} /> {tour.location}</span>
          <span><FontAwesomeIcon icon={faClock} /> {tour.duration}</span>
          <span><FontAwesomeIcon icon={faMoneyBill} /> {tour.price}</span>
        </div>
      </div>

      <div className="tour-content">
        <section className="tour-description">
          <h2>About This Tour</h2>
          <p>{tour.description}</p>
        </section>

        <section className="tour-highlights">
          <h2>Tour Highlights</h2>
          <ul>
            {tour.highlights.map((highlight, index) => (
              <li key={index}>
                <FontAwesomeIcon icon={faCheck} />
                {highlight}
              </li>
            ))}
          </ul>
        </section>

        <section className="tour-included">
          <h2>What's Included</h2>
          <ul>
            {tour.included.map((item, index) => (
              <li key={index}>
                <FontAwesomeIcon icon={faCheck} />
                {item}
              </li>
            ))}
          </ul>
        </section>

        <section className="tour-itinerary">
          <h2>Itinerary</h2>
          <div className="itinerary-timeline">
            {tour.itinerary.map((day) => (
              <div key={day.day} className="itinerary-day">
                <div className="day-number">Day {day.day}</div>
                <div className="day-content">
                  <h3>{day.title}</h3>
                  <p>{day.activities}</p>
                </div>
              </div>
            ))}
          </div>
        </section>

        <div className="booking-section">
          <button className="book-button">Book This Tour</button>
        </div>
      </div>
    </div>
  );
}

export default TourDetails;
