import { Link, useLocation } from "react-router-dom";
import logoJpg from "../logo.jpg";

const linksList = [
  { id: 1, text: "Home", url: "/" },
  { id: 2, text: "About", url: "/about" },
  { id: 3, text: "Tours", url: "/tours" },
  { id: 4, text: "Services", url: "/services" },
  { id: 5, text: "Blog", url: "/blog" },
  { id: 6, text: "Contact", url: "/contact" },
];

function Header({ activeLink, setActiveLink }) {
  const location = useLocation();

  const pathToLinkText = {
    "/": "Home",
    "/about": "About",
    "/tours": "Tours",
    "/services": "Services",
    "/blog": "Blog",
    "/contact": "Contact",
  };

  const currentActiveLink = pathToLinkText[location.pathname] || "";
  return (
    <header className="header">
      <div>
        <Link to="/" onClick={() => setActiveLink("Home")}>
          <img className="logo" src={logoJpg} alt="Logo" />
        </Link>
      </div>

      <div className="main-menu">
        <ul className="links-list">
          {linksList.map((link) => (
            <li key={link.id}>
              <Link
                className={currentActiveLink === link.text ? "active" : ""}
                to={link.url}
                onClick={() => {
                  setActiveLink(link.text);
                }}
              >
                {link.text}
              </Link>
            </li>
          ))}
        </ul>
      </div>
    </header>
  );
}

export default Header;
