import logo from "./logo.svg";
import logoJpg from "./logo.jpg";
import "./App.css";
import { useState } from "react";

const linksList = [
  { id: 1, text: "Home", url: "#" },
  { id: 2, text: "About", url: "#" },
  { id: 3, text: "Tours", url: "#" },
  { id: 4, text: "Services", url: "#" },
  { id: 5, text: "Blog", url: "#" },
  { id: 6, text: "Contact", url: "#" },
];

export default function App() {
  const [activeLink, setActiveLink] = useState('Home')
  return (
    <div className="App">
      <Header activeLink= {activeLink} 
      setActiveLink= {setActiveLink} />
    </div>
  );
}

function Header({activeLink, setActiveLink}) {
  return (
    <header className="header">
      <div>
        <img className="logo" src={logoJpg} alt="Logo" />
      </div>

      <div className="main-menu">
        <ul className="links-list">
          {linksList.map((link) => (
            <li key={link.id}>
              <a className= {activeLink=== link.text? 'active': ''}
               href={link.url}
               onClick= {(e)=>{e.preventDefault();
               setActiveLink(link.text)}}>{link.text}</a>
            </li>
          ))}
        </ul>
      </div>
    </header>
  );
}
