import { useEffect } from "react";
import { useLocation } from "react-router-dom";

function JumpToTop() {
  const scrollToTop = () => {
    const { pathname } = useLocation();

    useEffect(() => {
      window.scrollTo(0, 0);
    }, [pathname]);
    return null;
  };
}

export default JumpToTop;
